import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { MoreHorizontal, Play, Pause, CheckCircle2 } from 'lucide-react';

const STATUS_STYLES = {
  pending: 'bg-slate-500/10 text-slate-400',
  running: 'bg-blue-500/10 text-blue-400',
  completed: 'bg-emerald-500/10 text-emerald-400',
  failed: 'bg-red-500/10 text-red-400',
} as const;

export function StoryRow({ story }) {
  return (
    <div className="flex items-center justify-between p-4 border-b hover:bg-muted/50 transition-colors">
      <div className="flex items-center gap-3">
        <div className="p-2 rounded-lg bg-primary/10">
          <CheckCircle2 className="h-4 w-4 text-primary" />
        </div>
        <div>
          <p className="font-medium text-sm">{story.title}</p>
          <p className="text-xs text-muted-foreground">{story.epic}</p>
        </div>
      </div>
      <div className="flex items-center gap-4">
        <Badge className={STATUS_STYLES[story.status]}>
          {story.status}
        </Badge>
        <div className="flex gap-1">
          <Button variant="ghost" size="icon" className="h-8 w-8">
            {story.status === 'running' ? (
              <Pause className="h-4 w-4" />
            ) : (
              <Play className="h-4 w-4" />
            )}
          </Button>
          <Button variant="ghost" size="icon" className="h-8 w-8">
            <MoreHorizontal className="h-4 w-4" />
          </Button>
        </div>
      </div>
    </div>
  );
}

import { Badge } from '@/components/ui/badge';
import { cn } from '@/lib/utils';

// Status color map for consistent usage
export const STATUS_COLORS = {
  pending: {
    badge: 'bg-slate-500/10 text-slate-400 border-slate-500/20',
    dot: 'bg-slate-500',
  },
  in_progress: {
    badge: 'bg-blue-500/10 text-blue-400 border-blue-500/20',
    dot: 'bg-blue-500 animate-pulse',
  },
  completed: {
    badge: 'bg-emerald-500/10 text-emerald-400 border-emerald-500/20',
    dot: 'bg-emerald-500',
  },
  failed: {
    badge: 'bg-red-500/10 text-red-400 border-red-500/20',
    dot: 'bg-red-500',
  },
  warning: {
    badge: 'bg-amber-500/10 text-amber-400 border-amber-500/20',
    dot: 'bg-amber-500',
  },
} as const;

export function StatusBadge({ status }: { status: keyof typeof STATUS_COLORS }) {
  const colors = STATUS_COLORS[status];
  return (
    <Badge className={cn('capitalize', colors.badge)}>
      <span className={cn('h-1.5 w-1.5 rounded-full mr-2', colors.dot)} />
      {status.replace('_', ' ')}
    </Badge>
  );
}
