import { useState } from 'react';
import { Card, CardHeader, CardTitle, CardContent, CardFooter } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Avatar, AvatarImage, AvatarFallback } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';
import { Calendar, Clock, MapPin, Star, ChevronRight, Check } from 'lucide-react';

const steps = ['Specialty', 'Provider', 'Date & Time', 'Confirm'];

export function AppointmentScheduler() {
  const [step, setStep] = useState(0);
  const [specialty, setSpecialty] = useState('');
  const [provider, setProvider] = useState(null);
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');

  return (
    <div className="max-w-2xl mx-auto p-6">
      {/* Progress Steps */}
      <div className="flex items-center justify-between mb-8">
        {steps.map((s, i) => (
          <div key={s} className="flex items-center">
            <div className={`
              w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium
              ${i < step ? 'bg-[#10b981] text-white' : 
                i === step ? 'bg-[#0e203a] text-white' : 
                'bg-gray-200 text-gray-500'}
            `}>
              {i < step ? <Check className="h-4 w-4" /> : i + 1}
            </div>
            <span className={`ml-2 text-sm ${i === step ? 'font-medium text-[#0e203a]' : 'text-muted-foreground'}`}>
              {s}
            </span>
            {i < steps.length - 1 && (
              <ChevronRight className="h-4 w-4 mx-4 text-muted-foreground" />
            )}
          </div>
        ))}
      </div>

      {/* Step Content */}
      <Card>
        <CardHeader>
          <CardTitle className="text-[#0e203a]">
            {step === 0 && 'Select a Specialty'}
            {step === 1 && 'Choose Your Provider'}
            {step === 2 && 'Pick a Date & Time'}
            {step === 3 && 'Confirm Appointment'}
          </CardTitle>
        </CardHeader>
        <CardContent>
          {step === 0 && (
            <Select value={specialty} onValueChange={setSpecialty}>
              <SelectTrigger className="w-full">
                <SelectValue placeholder="Select specialty" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="primary">Primary Care</SelectItem>
                <SelectItem value="cardiology">Cardiology</SelectItem>
                <SelectItem value="orthopedics">Orthopedics</SelectItem>
                <SelectItem value="dermatology">Dermatology</SelectItem>
                <SelectItem value="neurology">Neurology</SelectItem>
              </SelectContent>
            </Select>
          )}

          {step === 1 && (
            <div className="space-y-4">
              {[1, 2, 3].map((i) => (
                <div
                  key={i}
                  onClick={() => setProvider(i)}
                  className={`
                    p-4 rounded-lg border cursor-pointer transition-all
                    ${provider === i ? 'border-[#0e203a] bg-[#f5f7fa]' : 'border-gray-200 hover:border-[#0e203a]'}
                  `}
                >
                  <div className="flex items-center gap-4">
                    <Avatar className="h-16 w-16">
                      <AvatarFallback className="bg-[#0e203a] text-white text-lg">DR</AvatarFallback>
                    </Avatar>
                    <div className="flex-1">
                      <h3 className="font-semibold">Dr. Sarah Smith, MD</h3>
                      <p className="text-sm text-muted-foreground">Internal Medicine</p>
                      <div className="flex items-center gap-1 mt-1">
                        <Star className="h-4 w-4 fill-[#e0a42e] text-[#e0a42e]" />
                        <span className="text-sm font-medium">4.9</span>
                        <span className="text-sm text-muted-foreground">(127 reviews)</span>
                      </div>
                    </div>
                    <Badge variant="outline" className="border-green-500 text-green-600">
                      Available Today
                    </Badge>
                  </div>
                </div>
              ))}
            </div>
          )}

          {step === 2 && (
            <div className="space-y-6">
              <div className="grid grid-cols-7 gap-2">
                {['Su', 'Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa'].map(day => (
                  <div key={day} className="text-center text-sm font-medium text-muted-foreground py-2">
                    {day}
                  </div>
                ))}
                {Array.from({ length: 31 }, (_, i) => i + 1).map(day => (
                  <button
                    key={day}
                    onClick={() => setDate(`2025-01-${day}`)}
                    disabled={day < 15 || [4, 5, 11, 12, 18, 19, 25, 26].includes(day)}
                    className={`
                      p-2 rounded-lg text-sm transition-colors
                      ${date === `2025-01-${day}` ? 'bg-[#0e203a] text-white' : 
                        day < 15 ? 'text-gray-300 cursor-not-allowed' :
                        [4, 5, 11, 12, 18, 19, 25, 26].includes(day) ? 'text-gray-300 cursor-not-allowed' :
                        'hover:bg-[#f5f7fa]'}
                    `}
                  >
                    {day}
                  </button>
                ))}
              </div>

              {date && (
                <div>
                  <h4 className="font-medium mb-3">Available Times</h4>
                  <div className="grid grid-cols-4 gap-2">
                    {['9:00 AM', '10:00 AM', '11:00 AM', '2:00 PM', '3:00 PM', '4:00 PM'].map(t => (
                      <button
                        key={t}
                        onClick={() => setTime(t)}
                        className={`
                          py-2 px-3 rounded-lg text-sm border transition-colors
                          ${time === t ? 'bg-[#0e203a] text-white border-[#0e203a]' : 'border-gray-200 hover:border-[#0e203a]'}
                        `}
                      >
                        {t}
                      </button>
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}

          {step === 3 && (
            <div className="space-y-4">
              <div className="bg-[#f5f7fa] rounded-lg p-4 space-y-3">
                <div className="flex items-center gap-3">
                  <Calendar className="h-5 w-5 text-[#0e203a]" />
                  <span>January 15, 2025</span>
                </div>
                <div className="flex items-center gap-3">
                  <Clock className="h-5 w-5 text-[#0e203a]" />
                  <span>2:00 PM</span>
                </div>
                <div className="flex items-center gap-3">
                  <MapPin className="h-5 w-5 text-[#0e203a]" />
                  <span>Ochsner Medical Center, New Orleans</span>
                </div>
              </div>
              <div className="flex items-center gap-4 p-4 border rounded-lg">
                <Avatar className="h-12 w-12">
                  <AvatarFallback className="bg-[#0e203a] text-white">SS</AvatarFallback>
                </Avatar>
                <div>
                  <h4 className="font-medium">Dr. Sarah Smith, MD</h4>
                  <p className="text-sm text-muted-foreground">Internal Medicine</p>
                </div>
              </div>
            </div>
          )}
        </CardContent>
        <CardFooter className="flex justify-between">
          <Button 
            variant="outline" 
            onClick={() => setStep(step - 1)}
            disabled={step === 0}
          >
            Back
          </Button>
          <Button 
            className="bg-[#0e203a] hover:bg-[#142d50]"
            onClick={() => step < 3 ? setStep(step + 1) : null}
            disabled={
              (step === 0 && !specialty) ||
              (step === 1 && !provider) ||
              (step === 2 && (!date || !time))
            }
          >
            {step === 3 ? 'Confirm Appointment' : 'Continue'}
          </Button>
        </CardFooter>
      </Card>
    </div>
  );
}

import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { FileText, Download, ChevronRight, AlertCircle, CheckCircle, TrendingUp, TrendingDown } from 'lucide-react';

export function TestResults() {
  const results = [
    { name: 'Glucose, Fasting', value: 95, unit: 'mg/dL', range: '70-100', status: 'normal', trend: 'stable' },
    { name: 'Hemoglobin A1c', value: 5.4, unit: '%', range: '<5.7', status: 'normal', trend: 'down' },
    { name: 'Cholesterol, Total', value: 215, unit: 'mg/dL', range: '<200', status: 'high', trend: 'up' },
    { name: 'HDL Cholesterol', value: 55, unit: 'mg/dL', range: '>40', status: 'normal', trend: 'stable' },
    { name: 'LDL Cholesterol', value: 135, unit: 'mg/dL', range: '<100', status: 'high', trend: 'up' },
  ];

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'normal':
        return <Badge variant="outline" className="border-green-500 text-green-600">Normal</Badge>;
      case 'high':
        return <Badge variant="outline" className="border-red-500 text-red-600">High</Badge>;
      case 'low':
        return <Badge variant="outline" className="border-yellow-500 text-yellow-600">Low</Badge>;
      default:
        return null;
    }
  };

  const getTrendIcon = (trend: string) => {
    switch (trend) {
      case 'up':
        return <TrendingUp className="h-4 w-4 text-red-500" />;
      case 'down':
        return <TrendingDown className="h-4 w-4 text-green-500" />;
      default:
        return null;
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-[#0e203a]">Lab Results</h1>
          <p className="text-muted-foreground">Comprehensive Metabolic Panel â€¢ December 20, 2024</p>
        </div>
        <Button variant="outline" className="gap-2">
          <Download className="h-4 w-4" />
          Download PDF
        </Button>
      </div>

      {/* Alert for abnormal results */}
      <Alert className="border-[#e0a42e] bg-amber-50">
        <AlertCircle className="h-4 w-4 text-[#e0a42e]" />
        <AlertDescription>
          <span className="font-medium">2 results require attention.</span> Your cholesterol levels are above the recommended range. 
          <Button variant="link" className="p-0 h-auto text-[#0e203a]">Message your doctor</Button>
        </AlertDescription>
      </Alert>

      {/* Results Table */}
      <Card>
        <CardHeader>
          <CardTitle className="text-[#0e203a] flex items-center gap-2">
            <FileText className="h-5 w-5" />
            Test Results
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-1">
            {/* Header Row */}
            <div className="grid grid-cols-12 gap-4 px-4 py-2 text-sm font-medium text-muted-foreground border-b">
              <div className="col-span-4">Test</div>
              <div className="col-span-2 text-right">Result</div>
              <div className="col-span-2">Trend</div>
              <div className="col-span-2">Reference</div>
              <div className="col-span-2">Status</div>
            </div>

            {/* Result Rows */}
            {results.map((result, index) => (
              <div 
                key={index}
                className={`
                  grid grid-cols-12 gap-4 px-4 py-3 rounded-lg
                  ${result.status !== 'normal' ? 'bg-red-50' : 'hover:bg-gray-50'}
                `}
              >
                <div className="col-span-4 font-medium">{result.name}</div>
                <div className="col-span-2 text-right font-mono">
                  <span className={result.status !== 'normal' ? 'text-red-600 font-semibold' : ''}>
                    {result.value}
                  </span>
                  <span className="text-muted-foreground ml-1">{result.unit}</span>
                </div>
                <div className="col-span-2 flex items-center gap-1">
                  {getTrendIcon(result.trend)}
                  <span className="text-sm text-muted-foreground capitalize">{result.trend}</span>
                </div>
                <div className="col-span-2 text-muted-foreground">{result.range}</div>
                <div className="col-span-2">{getStatusBadge(result.status)}</div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Provider Note */}
      <Card>
        <CardHeader>
          <CardTitle className="text-[#0e203a]">Provider Notes</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-muted-foreground">
            Your glucose and A1c levels look great! Your cholesterol is slightly elevated. 
            I recommend continuing your current diet and exercise routine. Let's recheck in 3 months. 
            Consider reducing saturated fat intake.
          </p>
          <div className="flex items-center gap-3 mt-4 pt-4 border-t">
            <div className="h-10 w-10 rounded-full bg-[#0e203a] flex items-center justify-center text-white font-medium">
              SS
            </div>
            <div>
              <p className="font-medium">Dr. Sarah Smith, MD</p>
              <p className="text-sm text-muted-foreground">Internal Medicine</p>
            </div>
            <Button variant="outline" className="ml-auto gap-2">
              Send Message <ChevronRight className="h-4 w-4" />
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}
