/**
 * Google Analytics Data API Client (Mock)
 * 
 * This client connects to the local mock GA Data API.
 * Replace the base URL with the real GA API in production.
 */

const MOCK_GA_API_BASE = '/api/mock-ga/v1beta';
const PROPERTY_ID = process.env.NEXT_PUBLIC_GA_PROPERTY_ID || '123456789';

export interface GADimension {
  name: string;
}

export interface GAMetric {
  name: string;
}

export interface GADateRange {
  startDate: string;
  endDate: string;
}

export interface GAReportRequest {
  dimensions?: GADimension[];
  metrics?: GAMetric[];
  dateRanges?: GADateRange[];
  limit?: number;
}

export interface GAReportRow {
  dimensionValues: { value: string }[];
  metricValues: { value: string }[];
}

export interface GAReportResponse {
  dimensionHeaders: { name: string }[];
  metricHeaders: { name: string; type: string }[];
  rows: GAReportRow[];
  rowCount: number;
  metadata: {
    currencyCode: string;
    timeZone: string;
  };
}

export async function getMetadata(): Promise<any> {
  const res = await fetch(`${MOCK_GA_API_BASE}/properties/${PROPERTY_ID}/metadata`);
  if (!res.ok) throw new Error('Failed to fetch metadata');
  return res.json();
}

export async function runReport(request: GAReportRequest): Promise<GAReportResponse> {
  const res = await fetch(`${MOCK_GA_API_BASE}/properties/${PROPERTY_ID}:runReport`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(request),
  });
  if (!res.ok) throw new Error('Failed to run report');
  return res.json();
}

export async function runRealtimeReport(request: GAReportRequest): Promise<GAReportResponse> {
  const res = await fetch(`${MOCK_GA_API_BASE}/properties/${PROPERTY_ID}:runRealtimeReport`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(request),
  });
  if (!res.ok) throw new Error('Failed to run realtime report');
  return res.json();
}

export async function batchRunReports(requests: GAReportRequest[]): Promise<{ reports: GAReportResponse[] }> {
  const res = await fetch(`${MOCK_GA_API_BASE}/properties/${PROPERTY_ID}:batchRunReports`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ requests }),
  });
  if (!res.ok) throw new Error('Failed to run batch reports');
  return res.json();
}

// Common report shortcuts
export async function getTopCountries(limit = 10) {
  return runReport({
    dimensions: [{ name: 'country' }],
    metrics: [{ name: 'activeUsers' }, { name: 'sessions' }],
    limit,
  });
}

export async function getTopPages(limit = 10) {
  return runReport({
    dimensions: [{ name: 'pagePath' }],
    metrics: [{ name: 'screenPageViews' }, { name: 'activeUsers' }],
    limit,
  });
}

export async function getDeviceBreakdown() {
  return runReport({
    dimensions: [{ name: 'deviceCategory' }],
    metrics: [{ name: 'activeUsers' }, { name: 'sessions' }, { name: 'bounceRate' }],
  });
}

export async function getTrafficSources(limit = 10) {
  return runReport({
    dimensions: [{ name: 'sessionSource' }, { name: 'sessionMedium' }],
    metrics: [{ name: 'sessions' }, { name: 'activeUsers' }],
